actions.script:
if (locale == null)
    locale = ec.l10n.locale
baseLocation = "component://webroot/screen/webroot/libs/jquery-validate/localization"
localizedScriptList = []
if (locale == null)
    return
spl = locale.toString().split('_')
language = spl.size() > 0? spl[0]: null
region = spl.size() > 1? spl[1]: null
for (type in ['messages', 'methods']) {
    res = ec.resource.getLocationReference("${baseLocation}/${type}_${language}_${region}.min.js")
    if (res == null || !res.exists)
        res = ec.resource.getLocationReference("${baseLocation}/${type}_${language}.min.js")
    if (res!= null && res.exists)
        localizedScriptList += [res.location.replace("component://webroot/screen/webroot", "")]
}
